import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';

const CartButton = ({ userId }) => {
    const [cartItems, setCartItems] = useState([]);
    const [show, setShow] = useState(false);

    // Fetch cart items from backend
    const fetchCartItems = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/cart/items?userId=${userId}`);
            const data = await response.json();

            // Transform data to include total price
            const itemsWithTotal = data.map(item => ({
                ...item,
                total: item.price * item.quantity,
            }));

            setCartItems(itemsWithTotal);
        } catch (error) {
            console.error('Error fetching cart items:', error);
        }
    };

    // Toggle Modal
    const toggleModal = () => setShow(!show);

    // Update quantity
    const updateQuantity = (id, delta) => {
        const updatedCart = cartItems.map(item => {
            if (item.id === id) {
                const newQuantity = item.quantity + delta;
                if (newQuantity > 0) {
                    return { ...item, quantity: newQuantity, total: newQuantity * item.price };
                }
            }
            return item;
        }).filter(item => item.quantity > 0);
        setCartItems(updatedCart);
    };

    // Remove item
    const removeItem = (id) => {
        setCartItems(cartItems.filter(item => item.id !== id));
    };

    // Calculate total cart
    const totalCart = cartItems.reduce((acc, item) => acc + item.total, 0);

    // Fetch items when the component mounts
    useEffect(() => {
        fetchCartItems();
    }, []);

    return (
        <>
            <button className="btn btn-primary" onClick={toggleModal}>
                <i className="fa fa-shopping-cart"></i> Cart
            </button>

            {show && (
                <div className="modal fade show d-block" style={{ backgroundColor: 'rgba(0, 0, 0, 0.5)' }}>
                    <div className="modal-dialog modal-lg">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">Your Cart</h5>
                                <button className="btn-close" onClick={toggleModal}></button>
                            </div>
                            <div className="modal-body">
                                {cartItems.length > 0 ? (
                                    cartItems.map(item => (
                                        <div key={item.id} className="d-flex align-items-center mb-3">
                                            <img
                                                src={`/images/products/${item.productId}.png`}
                                                alt={item.productName}
                                                style={{ width: 50, height: 50, marginRight: 10 }}
                                            />
                                            <div className="flex-grow-1">
                                                <h6>{item.productName}</h6>
                                                <p>
                                                    ${item.price.toFixed(2)} x {item.quantity} = ${item.total.toFixed(2)}
                                                </p>
                                            </div>
                                            <div>
                                                <button
                                                    className="btn btn-sm btn-danger"
                                                    onClick={() => updateQuantity(item.id, -1)}
                                                >-</button>
                                                <button
                                                    className="btn btn-sm btn-success mx-2"
                                                    onClick={() => updateQuantity(item.id, 1)}
                                                >+</button>
                                                <button
                                                    className="btn btn-sm btn-warning"
                                                    onClick={() => removeItem(item.id)}
                                                >Delete</button>
                                            </div>
                                        </div>
                                    ))
                                ) : (
                                    <p>Your cart is empty.</p>
                                )}
                            </div>
                            <div className="modal-footer">
                                <div className="flex-grow-1 text-start">
                                    <h5>Total: ${totalCart.toFixed(2)}</h5>
                                </div>
                                <button className="btn btn-primary" onClick={() => alert('Purchase finalized!')}>
                                    Finalize Purchase
                                </button>
                                <button className="btn btn-secondary" onClick={toggleModal}>
                                    Continue Shopping
                                </button>
                                <button className="btn btn-danger" onClick={() => setCartItems([])}>
                                    Cancel
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </>
    );
};

export default CartButton;
