import React from 'react';
import ProductAddItem from '../components/ProductAddItem';


function ProductItem({ product, name, price, image }) {
    return (
        <div className="box_main">
            <h4 className="shirt_text">{name}</h4>
            <p className="price_text">
                Price <span style={{ color: '#262626' }}>${price}</span>
            </p>
            <div className="tshirt_img">
                <img src={image} alt={name} />
            </div>
            <div className="btn_main">
                <div className="buy_bt">
                    <ProductAddItem key={product.id} product={product} />
                </div>
                <div className="seemore_bt">
                    <a href="#">See More</a>
                </div>
            </div>
        </div>
    );
}

function ProductSection({ nameController, fashionData }) {
    return (
        <div className="fashion_section">
            <div id={nameController} className="carousel slide" data-bs-ride="carousel">
                <div className="carousel-inner">
                    {fashionData.items.map((item, index) => (
                        <div
                            className={`carousel-item ${index === 0 ? 'active' : ''}`}
                            key={index}
                        >
                            <div className="container">
                                <h1 className="fashion_taital">{fashionData.title}</h1>
                                <div className="fashion_section_2">
                                    <div className="row">
                                        {item.products.map((product, productIndex) => (
                                            <div className="col-lg-4 col-sm-4" key={productIndex}>
                                                <ProductItem
                                                    product={product}
                                                    name={product.name}
                                                    price={product.price}
                                                    image={product.image}
                                                />
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>

                {/* Controles do Carrossel */}
                <button
                    className="carousel-control-prev"
                    type="button"
                    data-bs-target={"#" + nameController}
                    data-bs-slide="prev"
                >
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Previous</span>
                </button>

                <button
                    className="carousel-control-next"
                    type="button"
                    data-bs-target={"#" + nameController}
                    data-bs-slide="next"
                >
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Next</span>
                </button>
            </div>
        </div>
    );
}

export default ProductSection;
