import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom'; // Importa useParams
import Header from '../../components/Header';
import Footer from '../../components/Footer';
import axios from 'axios';

const UserForm = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [user, setUser] = useState({ id: 0, name: '', email: '', password: '' });
    const [message, setMessage] = useState('');

    useEffect(() => {

        console.warn(" id ", id);

        const fetchUser = async () => {
            if (id && id !== '0') {
                try {
                    const response = await axios.get(`http://localhost:8080/api/users/${id}`);
                    setUser(response.data);
                } catch (error) {
                    console.error('Error fetching user:', error);
                    setMessage('Error fetching user data!');
                }
            }
        };
        fetchUser();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser({ ...user, [name]: value });
    };

    const handleSave = async () => {
        try {
            const apiUrl = 'http://localhost:8080/api/users';
            let response;

            if (!user.id || user.id === 0) {
                response = await axios.post(apiUrl, user);
                setMessage('User created successfully!');
            } else {
                response = await axios.put(`${apiUrl}/${user.id}`, user);
                setMessage('User updated successfully!');
            }

            setUser(response.data);
            setTimeout(() => {
                navigate('/admin/UserList');
            }, 2000);
        } catch (error) {
            console.error('Error saving user:', error);
            setMessage('Error saving user!');
        }
    };

    const handleCancel = () => {
        navigate('/admin/UserList');
    };

    return (
        <div className="container">
            <Header title="User Form" />
            <div className="mt-3">
                {message && <div className="alert alert-info">{message}</div>} {/* Exibe a mensagem */}
                <div className="mb-3">
                    <label className="form-label">Name</label>
                    <input
                        type="text"
                        className="form-control"
                        name="name"
                        value={user.name}
                        onChange={handleChange}
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Email</label>
                    <input
                        type="email"
                        className="form-control"
                        name="email"
                        value={user.email}
                        onChange={handleChange}
                    />
                </div>
                <div className="mb-3">
                    <label className="form-label">Password</label>
                    <input
                        type="password"
                        className="form-control"
                        name="password"
                        value={user.password}
                        onChange={handleChange}
                    />
                </div>
            </div>
            <Footer onSave={handleSave} onCancel={handleCancel} />
        </div>
    );
};

export default UserForm;
