import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminSidebar = () => {
    const navigate = useNavigate();
    const [activeMenu, setActiveMenu] = useState(null);

    const toggleMenu = (menu) => {
        setActiveMenu(activeMenu === menu ? null : menu);
    };

    return (
        <div
            className="vh-100 p-3"
            style={{
                width: '250px',
                backgroundColor: '#1f1f1f',
                color: '#ffffff',
                borderRight: '1px solid #444',
            }}
        >
            <h5 className="text-light mb-4">Admin Menu</h5>
            <ul className="list-unstyled">

                <li>
                    <button
                        className="btn btn-link text-decoration-none text-light w-100 text-start"
                        onClick={() => toggleMenu('cadastro')}
                    >
                        Cadastro
                    </button>
                    {activeMenu === 'cadastro' && (
                        <ul className="list-unstyled ps-3">
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none text-light w-100 text-start"
                                    onClick={() => navigate('/admin/cadastro/usuario')}
                                >
                                    Usuário
                                </button>
                            </li>
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none text-light w-100 text-start"
                                    onClick={() => navigate('/admin/cadastro/categoria')}
                                >
                                    Categoria
                                </button>
                            </li>
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none text-light w-100 text-start"
                                    onClick={() => navigate('/admin/cadastro/produto')}
                                >
                                    Produto
                                </button>
                            </li>
                        </ul>
                    )}
                </li>

                <li>
                    <button
                        className="btn btn-link text-decoration-none text-light w-100 text-start"
                        onClick={() => navigate('/admin/relatorios')}
                    >
                        Relatórios
                    </button>
                </li>

                <li>
                    <button
                        className="btn btn-link text-decoration-none text-light w-100 text-start"
                        onClick={() => toggleMenu('dashboard')}
                    >
                        Dashboard
                    </button>
                    {activeMenu === 'dashboard' && (
                        <ul className="list-unstyled ps-3">
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none text-light w-100 text-start"
                                    onClick={() => navigate('/admin/dashboard/1')}
                                >
                                    Dashboard 1
                                </button>
                            </li>
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none text-light w-100 text-start"
                                    onClick={() => navigate('/admin/dashboard/2')}
                                >
                                    Dashboard 2
                                </button>
                            </li>
                        </ul>
                    )}
                </li>
            </ul>
        </div>
    );
};

export default AdminSidebar;
