import { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
    const [users, setUsers] = useState([]); // Estado para armazenar usuários
    const [currentPage, setCurrentPage] = useState(0); // Página atual (inicia em 0)
    const [totalPages, setTotalPages] = useState(0); // Total de páginas

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const apiUrl = `http://localhost:8080/api/users?page=${currentPage}&size=10`; // Endpoint com paginação
                const response = await axios.get(apiUrl);
                const data = response.data;

                setUsers(data.content); // Define a lista de usuários (campo `content`)
                setTotalPages(data.totalPages); // Define o total de páginas retornado pela API
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };

        fetchUsers();
    }, [currentPage]); // Reexecuta o efeito quando `currentPage` muda

    const handlePageChange = (page) => {
        if (page >= 0 && page < totalPages) {
            setCurrentPage(page); // Atualiza a página atual
        }
    };

    return (
        <div className="container">
            <h1>User List</h1>
            <table className="table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Email</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination">
                <button
                    onClick={() => handlePageChange(currentPage - 1)}
                    className="btn btn-bg-secondary"
                >
                    New
                </button>
                <button
                    onClick={() => handlePageChange(currentPage - 1)}
                    disabled={currentPage === 0}
                    className="btn btn-primary"
                >
                    Previous
                </button>
                <span className="mx-3">Page {currentPage + 1} of {totalPages}</span>
                <button
                    onClick={() => handlePageChange(currentPage + 1)}
                    disabled={currentPage + 1 === totalPages}
                    className="btn btn-primary"
                >
                    Next
                </button>
            </div>
        </div>
    );
};

export default UserList;
