import React from 'react';
import { Routes, Route, useNavigate } from 'react-router-dom';
import AdminHeader from './components/AdminHeader';
import AdminSidebar from './components/AdminSidebar';
import AdminFooter from './components/AdminFooter';
import './AppAdm.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Dashboard from './pages/Dashboard';
import homeAdm from './pages/homeAdm';

function AppAdm() {
  const navigate = useNavigate();

  const user = {
    name: 'Vanderlei Soares de Oliveira',
    id: '012457',
    avatar: 'https://i.pinimg.com/736x/3e/aa/24/3eaa245d923949b6f662b8ba07b7a3b2.jpg',
  };

  const handleLogout = () => {
    navigate('/');
  };

  return (
    <div className="container-fluid" style={{ height: '100vh', display: 'flex', flexDirection: 'column', backgroundColor: 'green' }}>
      {/* Cabeçalho */}
      <header >
        <AdminHeader user={user} onLogout={handleLogout} />
      </header>

      <div className="row flex-grow-1" style={{ overflow: 'hidden', paddingLeft: '14px' }}>
        {/* Coluna Esquerda: Menu Lateral */}
        <nav className="col-md-3 col-lg-2 bg-light d-none d-md-block" style={{ padding: 0 }}>
          <AdminSidebar />
        </nav>

        {/* Coluna Direita: Conteúdo Principal */}
        <main className="col-md-9 col-lg-10 d-flex flex-column" style={{ overflowY: 'auto' }}>
          <div className="p-4 flex-grow-1" style={{ backgroundColor: 'white' }}>
            <Routes>
              <Route path="/" element={<homeAdm />} />
              <Route path="/dashboard" element={<Dashboard />} />
            </Routes>
          </div>

          {/* Rodapé */}
          <footer>
            <AdminFooter />
          </footer>
        </main>
      </div>
    </div>
  );
}

export default AppAdm;
