import React from 'react';

function ProductAddItem({ userId, product }) {
    //const userId = 1; // Simulação de usuário logado

    const addToCart = async () => {
        const response = await fetch(`http://localhost:8080/api/cart/add?userId=${userId}&productId=${product.id}&productName=${product.name}&price=${product.price}`);
        const data = await response.json();
        console.log('Produto adicionado ao carrinho:', data);
    };

    return (
        <div>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
            <button onClick={addToCart}>Buy Now</button>
        </div>
    );
}

export default ProductAddItem;
