import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminSidebar = () => {
    const navigate = useNavigate();
    const [activeMenu, setActiveMenu] = useState(null);

    const toggleMenu = (menu) => {
        setActiveMenu(activeMenu === menu ? null : menu); // Alterna entre abrir e fechar o submenu
    };

    return (
        <div
            className="vh-100 p-3"
            style={{
                width: '250px',
                backgroundColor: '#1f1f1f', // Cor de fundo do menu
                color: '#ffffff', // Cor do texto principal
                borderRight: '1px solid #444', // Borda à direita
            }}
        >
            {/* Título do Menu */}
            <h5
                style={{
                    color: '#ffffff',
                    borderBottom: '1px solid #444',
                    paddingBottom: '10px',
                    marginBottom: '20px',
                }}
            >
                Admin Menu
            </h5>

            {/* Lista de itens do menu */}
            <ul className="list-unstyled" style={{ margin: 0, padding: 0 }}>
                {/* Cadastro */}
                <li>
                    <button
                        className="btn btn-link text-decoration-none w-100 text-start"
                        onClick={() => toggleMenu('cadastro')}
                        style={{
                            color: '#ffffff',
                            padding: '10px',
                            background: 'none',
                            border: 'none',
                        }}
                    >
                        Cadastro
                    </button>
                    {activeMenu === 'cadastro' && (
                        <ul className="list-unstyled ps-3">
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none w-100 text-start"
                                    onClick={() => navigate('/admin/cadastro/usuario')}
                                    style={{
                                        color: '#cccccc',
                                        padding: '8px',
                                        background: 'none',
                                        border: 'none',
                                    }}
                                >
                                    Usuário
                                </button>
                            </li>
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none w-100 text-start"
                                    onClick={() => navigate('/admin/cadastro/categoria')}
                                    style={{
                                        color: '#cccccc',
                                        padding: '8px',
                                        background: 'none',
                                        border: 'none',
                                    }}
                                >
                                    Categoria
                                </button>
                            </li>
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none w-100 text-start"
                                    onClick={() => navigate('/admin/cadastro/produto')}
                                    style={{
                                        color: '#cccccc',
                                        padding: '8px',
                                        background: 'none',
                                        border: 'none',
                                    }}
                                >
                                    Produto
                                </button>
                            </li>
                        </ul>
                    )}
                </li>

                {/* Relatórios */}
                <li>
                    <button
                        className="btn btn-link text-decoration-none w-100 text-start"
                        onClick={() => navigate('/admin/relatorios')}
                        style={{
                            color: '#ffffff',
                            padding: '10px',
                            background: 'none',
                            border: 'none',
                        }}
                    >
                        Relatórios
                    </button>
                </li>

                {/* Dashboard */}
                <li>
                    <button
                        className="btn btn-link text-decoration-none w-100 text-start"
                        onClick={() => toggleMenu('dashboard')}
                        style={{
                            color: '#ffffff',
                            padding: '10px',
                            background: 'none',
                            border: 'none',
                        }}
                    >
                        Dashboard
                    </button>
                    {activeMenu === 'dashboard' && (
                        <ul className="list-unstyled ps-3">
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none w-100 text-start"
                                    onClick={() => navigate('/admin/dashboard/1')}
                                    style={{
                                        color: '#cccccc',
                                        padding: '8px',
                                        background: 'none',
                                        border: 'none',
                                    }}
                                >
                                    Dashboard 1
                                </button>
                            </li>
                            <li>
                                <button
                                    className="btn btn-link text-decoration-none w-100 text-start"
                                    onClick={() => navigate('/admin/dashboard/2')}
                                    style={{
                                        color: '#cccccc',
                                        padding: '8px',
                                        background: 'none',
                                        border: 'none',
                                    }}
                                >
                                    Dashboard 2
                                </button>
                            </li>
                        </ul>
                    )}
                </li>
            </ul>
        </div>
    );
};

export default AdminSidebar;
