import React from 'react';

function ProductItem({ product }) {
    const userId = 1; // Simulação de usuário logado

    const addToCart = async () => {
        const response = await fetch(`/api/cart/add?userId=${userId}&productId=${product.id}&productName=${product.name}&price=${product.price}`);
        const data = await response.json();
        console.log('Produto adicionado ao carrinho:', data);
    };

    return (
        <div>
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
            <button onClick={addToCart}>Adicionar à Sacola</button>
        </div>
    );
}

export default ProductItem;
